import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// Fetch files list from the API
export const fetchFiles = createAsyncThunk(
  'files/fetchFiles',
  async (_, { getState }) => {
    const token = getState().auth.token;
    console.log(token)
    const response = await fetch('http://localhost:8000/auth/files/list', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Add the Bearer token
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch files');
    }

    const data = await response.json();
    return data.files;
  }
);

// Upload file to the server
export const uploadFile = createAsyncThunk(
  'files/uploadFile',
  async (formData) => {
    const response = await fetch('http://localhost:8000/auth/files/upload', {
      method: 'POST',
      body: formData,
    });
    return response.json();
  }
);

// Share file with a user
export const shareFile = createAsyncThunk(
  'files/shareFile',
  async ({ fileId, data }) => {
    const response = await fetch(`http://localhost:8000/auth/files/share/${fileId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });
    return response.json();
  }
);

// Generate a secure link for the file
export const generateSecureLink = createAsyncThunk(
  'files/generateSecureLink',
  async (fileId, { getState }) => {
    const token = getState().auth.token;
    const response = await fetch(`http://localhost:8000/auth/share/generate_secure_link/${fileId}`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error('Failed to generate secure link');
    }

    const data = await response.json();
    return data.secureLink; // Assuming the API returns a `secureLink` field
  }
);

const initialState = {
  files: [],
  status: 'idle',
  error: null,
  secureLink: null, // New state to hold the generated secure link
};

const fileSlice = createSlice({
  name: 'files',
  initialState,
  reducers: {
    // other reducers here
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchFiles.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchFiles.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.files = action.payload; // Set the files to the fetched data
      })
      .addCase(fetchFiles.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(uploadFile.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(uploadFile.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.files.push(action.payload); // Assuming the server returns the newly uploaded file
      })
      .addCase(uploadFile.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(shareFile.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(shareFile.fulfilled, (state, action) => {
        state.status = 'succeeded';
        // handle the successful share action (e.g., update the file list)
      })
      .addCase(shareFile.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(generateSecureLink.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(generateSecureLink.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.secureLink = action.payload; // Set the generated secure link
      })
      .addCase(generateSecureLink.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  },
});

export default fileSlice.reducer;
