import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import CryptoJS from 'crypto-js';

const SECRET_KEY = CryptoJS.enc.Utf8.parse('123456789012345678901234');
const BLOCK_SIZE = 16; // AES block size

function decryptFile(encryptedHex) {
  try {
    // Decode hex string to bytes
    const encryptedBytes = CryptoJS.enc.Hex.parse(encryptedHex);
    const encryptedData = CryptoJS.lib.WordArray.create(encryptedBytes.words);

    // Extract IV and ciphertext
    const iv = CryptoJS.lib.WordArray.create(
      encryptedData.words.slice(0, BLOCK_SIZE / 4),
      BLOCK_SIZE
    );
    const ciphertext = CryptoJS.lib.WordArray.create(
      encryptedData.words.slice(BLOCK_SIZE / 4)
    );

    // Decrypt using AES-CBC
    const decrypted = CryptoJS.AES.decrypt(
      { ciphertext },
      SECRET_KEY,
      { iv: iv, mode: CryptoJS.mode.CBC, padding: CryptoJS.pad.Pkcs7 }
    );

    // Convert decrypted data to Blob or plain text
    const fileContent = CryptoJS.enc.Utf8.stringify(decrypted);
    return fileContent; // Or convert to Blob if needed
  } catch (error) {
    console.error('Decryption failed:', error);
    throw new Error('Failed to decrypt file content.');
  }
}

// Fetch files list from the API
export const fetchFiles = createAsyncThunk(
  'files/fetchFiles',
  async (_, { getState }) => {
    const token = getState().auth.token;
    const response = await fetch('http://localhost:8000/auth/files/list', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Add the Bearer token
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch files');
    }

    const data = await response.json();
    return data.files;
  }
);

// Upload file to the server
export const uploadFile = createAsyncThunk(
  'files/uploadFile',
  async (formData, { getState }) => {
    const token = getState().auth.token;
    const response = await fetch('http://localhost:8000/auth/files/upload', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
      body: formData,
    });

    if (!response.ok) {
      throw new Error('File upload failed');
    }

    return response.json();
  }
);

// Share file with a user
export const shareFile = createAsyncThunk(
  'files/shareFile',
  async ({ fileId, data }) => {
    const response = await fetch(`http://localhost:8000/auth/share/file/${fileId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });
    return response.json();
  }
);

// Generate a secure link for the file
export const generateSecureLink = createAsyncThunk(
  'files/generateSecureLink',
  async (fileId, { getState }) => {
    const token = getState().auth.token;
    const response = await fetch(`http://localhost:8000/auth/share_files/generate_secure_link/${fileId}/`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error('Failed to generate secure link');
    }

    const data = await response.json();
    console.log(data);
    return data.secureLink; // Assuming the API returns a `secureLink` field
  }
);

// Download a file
export const downloadFile = createAsyncThunk(
    'files/downloadFile',
    async (fileId, { getState }) => {
      const token = getState().auth.token;
      const response = await fetch(`http://localhost:8000/auth/files/download/${fileId}/`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to download the file');
      }

      // Assuming the response JSON contains `file_content` as hex string
      const data = await response.json();
      const encryptedHex = data.file_content;

      try {
        const decryptedFile = decryptFile(encryptedHex);
        const blob = new Blob([decryptedFile], { type: 'application/octet-stream' });

        // Trigger file download
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'decrypted_file.txt'; // Replace with a dynamic file name if needed
        document.body.appendChild(a);
        a.click();
        a.remove();
        window.URL.revokeObjectURL(url);

        return fileId; // Optionally return the fileId to update state
      } catch (error) {
        console.error('Decryption failed:', error);
        throw new Error('Failed to decrypt and download file.');
      }
    }
  );


const initialState = {
  files: [],
  status: 'idle',
  error: null,
  secureLink: null, // New state to hold the generated secure link
};

const fileSlice = createSlice({
  name: 'files',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchFiles.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchFiles.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.files = action.payload; // Set the files to the fetched data
      })
      .addCase(fetchFiles.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(uploadFile.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(uploadFile.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.files.push(action.payload); // Assuming the server returns the newly uploaded file
      })
      .addCase(uploadFile.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(shareFile.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(shareFile.fulfilled, (state, action) => {
        state.status = 'succeeded';
        // handle the successful share action (e.g., update the file list)
      })
      .addCase(shareFile.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(generateSecureLink.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(generateSecureLink.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.secureLink = action.payload; // Set the generated secure link
      })
      .addCase(generateSecureLink.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(downloadFile.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(downloadFile.fulfilled, (state) => {
        state.status = 'succeeded';
        // Handle post-download logic if needed
      })
      .addCase(downloadFile.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  },
});

export default fileSlice.reducer;
